package br.com.vitormarcal.thymelafpdfexample.pdf;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;

public class ZipUtilTest {

    @Test
    void dadoUmaStringValida_quandoExecutarCompress_entaoUmaStringEhRetornada() {
        // given
        final String str = "";
        final int strLength = str.length();

        // when
        String strCompress = ZipUtil.compress(str);

        // then
        Assertions.assertTrue(strLength > strCompress.length());
        System.out.println("######");
        System.out.println(strCompress);
        System.out.println("######");
    }

}
